import React, {Component} from 'react'
import { Container, Header, Segment, Card, Button, CardHeader, CardMeta, CardContent } from 'semantic-ui-react';

import config, {auth, providers} from './../Config'
import Navegacao from './Navegacao'


class Inicio extends Component {

    constructor (props){
        super (props)

        this.state = {
            usuario: {}, 
            estaLogado: false
        }

        auth.onAuthStateChanged((usuario) => {
            if(usuario){
                console.log(usuario)
                this.state({
                    usuario,
                    estaLogado: true
                })
            }else{
                console.log('Nao Logou')
                this.state({estaLogado:false})

            }
        })


    }
    autenticar(provider){
        console.log(provider)
        auth.signInWithPopup(providers[provider])

    }



    render(){
        return (
            <div>
                <Navegacao />
                <Container>
                    <Segment piled>
                        Quiz
                    </Segment>
                    <Header as='h2'>
                        Jogo de pergunta e respostas
                    </Header>
                    <p>
                        Desafie seus amigos nesse incrível jogo de perguntas e respotas
                    </p>
                    <Card fluid> 
                        <CardContent>
                            Acesse agora mesmo
                        </CardContent>
                        <CardContent>
                            <button  color='facebook' onClick={() => this.autenticar('facebook')}>Login com Facebook</button>
                            <button  color='google' onClick={() => this.autenticar('google')}>Login com Google</button>
                            <button color='green'>Usuário Adiministrador</button>
                        </CardContent>

                    </Card>
                </Container>           
            </div>
        )    

    }


}

export default Inicio
